import { useState } from "react";
import React from "react";
import Editors from "./Editors";

function App() {
  const [html, setHtml]=useState('')
  return (
    <div>
      <div className="pane top-pane">
        <Editors 
        language='xml' 
        displayName="HTML"
        value={html}
        onchange={setHtml}
         /> 
        <Editors />
        <Editors />
      </div>
      <div className="pane">
        <iframe
        title="output"
        sandbox="allow-scripts"
        frameBorder="0"
        width="100%"
        height="100%"
        />
      </div>
    </div>
  );
}

export default App;
