import { useState } from "react";
import React from "react";
import Editors from "./Editors";

function App() {
  const [html, setHtml]=useState('')
  const [css, setCss]=useState('')
  const [js, setJs]=useState('')

  const srcDoc=`
  <html>
  <body>${html}</body>
  <style>${css}</style>
  <script>${js}</script>
  </html>
  `

  return (
    <>
      <div className="pane top-pane">
        <Editors 
        language="xml"
        displayName="HTML"
        value={html}
        onChange={setHtml}
         /> 
        <Editors
         language="css"
         displayName="CSS"
         value={css}
         onChange={setCss}
          />
        <Editors 
         language="javascript"
         displayName="JS"
         value={js}
         onChange={setJs}
         />
      </div>
      <div className="pane">
        <iframe
        title="output"
        sandbox="allow-scripts"
        frameBorder="0"
        width="100%"
        height="100%"
        />
      </div>
    </>
  );
}

export default App;
